// export default reducer;

const reducer = (state, action) => {
  switch (action.type) {
    case "GET_STORIES": {
      return {
        ...state,
        hits: action.payload.hits,
        nbPages: action.payload.nbPages,
      };
    }
  }
  return state;
};

export default reducer;
